mapper.update:
UPDATE BOARD SET
READ_COUNT = READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO BOARD_FILE SELECT SEQ_BNO.NEXTVAL, A. FROM (
SELECT #{item.filePath} FILE_PATH, #{item.fileName} FILE_NAME, #{item.parentBoardNo} PARENT_BOARD_NO FROM DUAL
)A

mapper.update:
UPDATE BOARD SET
STATUS = 'Y'
WHERE BOARD_NO = #{boardNo}
