mapper.select:
SELECT * FROM V_LOGIN
WHERE MEM_ID = #{memberId}

AND SCSN_FL = 'Y'

SELECT COUNT(*) FROM MEMBER
WHERE MEM_ID = #{memberId}
AND SCSN_FL = 'Y'

SELECT COUNT(*) FROM MEMBER
WHERE NICKNAME = #{memberNickname}
AND SCSN_FL = 'Y'

SELECT COUNT(*) FROM MEMBER
WHERE MEM_NM = #{name}
AND EMAIL = #{mail}

mapper.update:

UPDATE MEMBER SET
NICKNAME = #{memberNickname},
PHONE = #{memberPhone}
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER SET
MEM_PW = #{newPwd} 
WHERE MEM_NO = #{memberNo}

UPDATE MEMBER SET
SCSN_FL = 'N'
WHERE MEM_NO=#{memberNo}
